mapper.select:
SELECT a.material_id, a.mat_type_code, a.sub_mat_type_code, a.in_date, a.in_time, a.model, a.tray_code, a.unit, a.in_qty, a.use_qty, a.remain_qty, a.use_yn, a.worker_id, a.worker_name, a.comments, b.out_comments, a.create_date, a.create_user, case when isnull(c.close_yn,'N') = 'N' and a.use_yn = 'N' then 'Y' else 'N' end as editable FROM dw_sub_mat_tray_in a left outer join ( SELECT a.material_id, a.in_date, STRING_AGG(a.comments, ' / ') AS out_comments FROM dw_sub_mat_tray_out a GROUP BY a.material_id, a.in_date ) b on ( a.material_id = b.material_id and a.in_date = b.in_date ) left outer join ( select * from dw_mat_close_info where mat_category_code ='20' and mat_type_code='TRAY' ) c on ( a.in_date = c.stock_date ) WHERE 1 = 1
and a.material_id = #{searchMaterialId}
and a.in_date between #{fromDate} and #{toDate}
AND a.sub_mat_type_code = #{subMatTypeCode}
order by a.in_date desc, a.in_time desc, a.material_id

select COALESCE(STRING_AGG(material_id, '/'), 'NoData') AS materialId from dw_sub_mat_tray_in a where upper(a.material_id)+in_date in
upper(#{item.materialId}) + #{item.inDate}
and a.use_yn = 'Y'

mapper.update:
UPDATE dw_sub_mat_tray_in
	SET sub_mat_type_code = #{subMatTypeCode},
		worker_name = #{workerName},
		in_qty = #{inQty},
		remain_qty = #{inQty},
		comments = #{comments}
WHERE material_id = #{materialId}
  and in_date = #{inDate}

UPDATE dw_sub_mat_tray_in
	SET use_qty = use_qty + #{outQty},
	    remain_qty = remain_qty - #{outQty},
	    use_yn = 'Y'
WHERE material_id = #{materialId}
  and in_date = #{inDate}

mapper.delete:
DELETE  FROM dw_sub_mat_tray_in
WHERE material_id = #{materialId}
  and in_date = #{inDate}

mapper.select:
SELECT a.line , a.설비번호 , a.설비명 , a.설비약명 , a.chamber , a.공정코드 , b.공정약어 as 공정명 , a.공정구분 as area_code , a.area , a.비고 FROM dw_equipment_mast a LEFT OUTER JOIN dw_step_mast b ON ( a.line = b.line and a.공정코드 = b.공정코드 ) where 1 = 1
and a.line = #{line}
and a.공정코드 IN
#{item}
and a.사용여부 = '1'

SELECT a.out_no, a.mat_type_code, a.sub_mat_type_code, c.code_name as sub_mat_type_name, a.work_date, a.shift_code, a.out_seq, a.out_date, a.out_time, a.material_id, a.in_date, a.model, a.worker_name, a.line, a.step_code, a.step_nm, a.equip_no, a.equip_nm, a.unit, isnull(b.remain_qty,0) remain_qty, isnull(b.remain_qty,0) + isnull(a.out_qty,0) as stock_qty, isnull(a.out_qty,0) out_qty, isnull(a.out_qty,0) as org_out_qty, isnull(a.use_qty,0) use_qty, isnull(a.error_qty,0) error_qty, a.comments, b.comments as in_comments, a.create_date, a.create_user, case when isnull(d.close_yn, 'N') = 'N' and
( ( datepart(hour,getdate()) < 8 and ( a.out_date = CONVERT(VARCHAR(8), getdate(), 112) ) or ( a.out_date = CONVERT(VARCHAR(8), DATEADD(DAY, -1, getdate()), 112) and convert(int,substring(a.out_time, 1,2)) > 19) ) or ( datepart(hour,getdate()) >= 8 and a.out_date = CONVERT(VARCHAR(8), getdate(), 112) ) or b.remain_qty > 0 ) then 'Y' else 'N' end editable FROM dw_sub_mat_tray_out a join dw_sub_mat_tray_in b on ( a.material_id = b.material_id and a.in_date = b.in_date) JOIN ( SELECT code, code_name from dw_common_code WHERE maj_code='81' ) c ON ( a.sub_mat_type_code = c.code ) left outer join ( select * from dw_mat_close_info where mat_category_code ='20' and mat_type_code='TRAY' ) d on ( a.work_date = d.stock_date ) where 1 = 1
and a.material_id = #{searchMaterialId}
and work_date between #{fromDate} and #{toDate}
and shift_code = #{shiftCode}
and line= #{line} and equip_no = #{equip}
order by a.out_date desc, a.out_time desc, a.out_seq desc

SELECT a.stock_date, a.material_id, a.mat_type_code, a.sub_mat_type_code, b.code_name as sub_mat_type_name, a.model, a.unit, a.prev_stock_qty, a.prev_real_stock_qty, a.in_qty, a.out_qty, a.use_qty, a.error_qty, a.stock_qty, a.real_stock_qty, a.diff_qty, a.confirm_yn, a.charger_id, a.charger_name, a.comments, case when c.status = 'CLOSE' then 'N' else 'Y' end as editable FROM dw_sub_mat_tray_id_stock a left outer join ( select code, code_name from dw_common_code where maj_code='81' ) b on ( a.sub_mat_type_code = b.code ) left outer join ( select * from dw_mat_close_info where mat_category_code='20' and mat_type_code='TRAY' ) c on ( a.stock_date = c.stock_date ) where a.stock_date = #{stockDate}
and a.material_id = #{materialId}
and a.sub_mat_type_code = #{subMatType}
and a.confirm_yn = #{confirmYn}
AND ( (#{diffYn} = 'N' AND a.diff_qty = 0) OR (#{diffYn} = 'Y' AND a.diff_qty != 0) )
order by a.stock_date, a.material_id

SELECT *
  INTO ${tempTableName}
  FROM dw_sub_mat_tray_id_stock
 WHERE stock_date = #{stockDate}

mapper.update:
update dw_sub_mat_tray_id_stock
   set charger_name = #{workerName}, 
   	   real_stock_qty = isnull(#{realStockQty},0),
	   diff_qty =  isnull(#{realStockQty},0) - stock_qty,
	   confirm_yn = #{confirmYn},
	   comments = #{comments}
where stock_date = #{stockDate}
  and material_id = #{materialId}

mapper.select:
WITH BaseData AS ( SELECT model, stock_date, sort_order, gubun, CASE WHEN gubun = 'in_qty' THEN '1' WHEN gubun = 'out_qty' THEN '2' WHEN gubun = 'loss_qty' THEN '3' WHEN gubun = 'use_qty' THEN '4' WHEN gubun = 'stock_qty' THEN '6' WHEN gubun = 'real_stock_qty' THEN '5' WHEN gubun = 'diff_qty' THEN '7' END AS item_order, CASE WHEN gubun = 'in_qty' THEN '입고' WHEN gubun = 'out_qty' THEN '투입' WHEN gubun = 'loss_qty' THEN '투입' WHEN gubun = 'use_qty' THEN '투입' ELSE '재고' END AS item_name1, CASE WHEN gubun = 'in_qty' THEN '' WHEN gubun = 'out_qty' THEN 'Total' WHEN gubun = 'loss_qty' THEN 'LOSS' WHEN gubun = 'use_qty' THEN '사용' WHEN gubun = 'stock_qty' THEN '전산' WHEN gubun = 'real_stock_qty' THEN '실물' WHEN gubun = 'diff_qty' THEN '차이' END AS item_name2, qty FROM ( SELECT model, stock_date, 1 AS sort_order, in_qty, out_qty, use_qty, error_qty as loss_qty, stock_qty, real_stock_qty, diff_qty FROM dw_sub_mat_tray_stock WHERE stock_date LIKE CONCAT(#{month}, '%') UNION ALL SELECT model, 'Total' AS stock_date, 1 AS sort_order, SUM(in_qty) AS in_qty, SUM(out_qty) AS out_qty, SUM(use_qty) AS use_qty, SUM(error_qty) AS loss_qty, SUM(stock_qty) AS stock_qty, SUM(real_stock_qty) AS real_stock_qty, SUM(diff_qty) AS diff_qty FROM dw_sub_mat_tray_stock WHERE stock_date LIKE CONCAT(#{month}, '%') GROUP BY model ) a UNPIVOT ( qty FOR gubun IN (in_qty, out_qty, use_qty, loss_qty, stock_qty, real_stock_qty, diff_qty) ) AS b ) SELECT a.*, case when a.item_name1 = '재고' then isnull(b.qty , 0) else null end as pre_stock FROM ( SELECT model, gubun, sort_order, item_order, item_name1, item_name2, case when item_name1 = '재고' then null else ISNULL([Total], 0) end AS D_Total,
ISNULL([${item.day}], 0) AS D_${item.day}
FROM BaseData PIVOT ( SUM(qty) FOR stock_date IN ([Total],[${item.day}]) ) AS pvt ) a LEFT OUTER JOIN ( SELECT model, gubun, qty FROM ( SELECT model, prev_stock_qty AS stock_qty, prev_real_stock_qty AS real_stock_qty, prev_real_stock_qty - prev_stock_qty AS diff_qty FROM dw_sub_mat_tray_stock WHERE stock_date = CONCAT(#{month}, '01') ) a UNPIVOT ( qty FOR gubun IN (stock_qty, real_stock_qty, diff_qty) ) AS b ) b ON ( a.model = b.model and a.gubun = b.gubun ) ORDER BY a.sort_order, model, item_order, item_name2

mapper.update:
UPDATE dw_tray_code_info
	SET model = #{model}
WHERE tray_code = #{trayCode}
