mapper.delete:
DELETE FROM dw_공정별_모니터링항목
WHERE LINE=#{line}
AND PROCESS_ID=#{processId} 
AND 공정코드=#{공정코드}
AND 모니터링구분=#{모니터링구분}
AND 모니터링항목=#{모니터링항목}
;

mapper.update:
UPDATE dw_공정별_모니터링항목
SET 공정명 = #{공정명},
	등급및수량 = #{등급및수량},
	검증부서 = #{검증부서},
	순서 = #{순서},
	설비유형_수동_대수 = #{설비유형수동대수},
	설비유형_자동_대수 = #{설비유형자동대수}
WHERE LINE=#{line}
AND PROCESS_ID=#{processId} 
AND 공정코드=#{공정코드}
AND 모니터링구분=#{모니터링구분}
AND 모니터링항목=#{모니터링항목}
;
